import { View, Text, StyleSheet, Pressable } from "react-native";

const Inputgrid = () => {
  return (
    <View style={styles.gridContainer}>
      <View style={styles.rowContainer}>
        <Text>1</Text>
        <Text>2</Text>
        <Text>3</Text>
        <Text>+</Text>
      </View>
      <View style={styles.rowContainer}>
        <Text>4</Text>
        <Text>5</Text>
        <Text>6</Text>
        <Text>-</Text>
      </View>
      <View style={styles.rowContainer}>
        <Text>7</Text>
        <Text>8</Text>
        <Text>9</Text>
        <Text>*</Text>
      </View>
      <View style={styles.rowContainer}>
        <Text>0</Text>
        <Text>.</Text>
        <Text>=</Text>
        <Text>/</Text>
      </View>
    </View>
  );
};

export default Inputgrid;

const styles = StyleSheet.create({
  gridContainer: {
    // temportary until spacing is correct
    backgroundColor: "blue",
    height: 350,
    width: "85%",
  },
  rowContainer: { flex: "row" },
});
